import React, { Component } from 'react'
import logo from '../../assets/images/covalue/marca_co_amarela.png';

export class HomePage extends Component {

  componentDidMount() {
    // Load animations when they intersect windows
    var circles = [].slice.call(document.querySelectorAll(".circle"));

    if ("IntersectionObserver" in window) {
      let lazyImageObserver = new IntersectionObserver(function(entries, observer) {
        entries.forEach(function(entry) {
          if (entry.isIntersecting) {
            let lazyImage = entry.target;
            lazyImage.src = lazyImage.dataset.src;
            lazyImage.classList.remove("lazy");
            lazyImageObserver.unobserve(lazyImage);
          }
        });
      });

      lazyImages.forEach(function(lazyImage) {
        lazyImageObserver.observe(lazyImage);
      });
    }
  }

  render() {
    let circle_right = {
      top: 0,
      right: 0,
      transform: 'translate(50%, -50%)'
    }
    return (
      <div className="home-background">
        <section className="banner">
          <div className="banner left"></div>
          <div className="banner right"><img src={logo} alt="covalue"/></div>
        </section>
        <section className="section-intro">
          <div className="circle" style={circle_right}>
            <div className="circle__half"></div>
            <div className="circle__half circle__half--right"></div>
          </div>
          <h3>
            Estamos aqui para melhorar negócios.<br />
            Com total integridade e transparência, representamos um upgrade estratégico
            que gera valor para a sua empresa onde você não sabia que existia.
          </h3>
        </section>
      </div>
    )
  }
}

export default HomePage
